from django.shortcuts import render
from django.http import Http404

# Create your views here.
posts=[
    {
        'id':1,
        'title':'HTML',
        'discription':'HTML is used to create a static web pages',
        'imageurl':'https://www.w3.org/html/logo/downloads/HTML5_Badge_512.png'
    },
    {
        'id':2,
        'title':'CSS',
        'discription':'CSS is used to style the web pages',
        'imageurl':'https://images.icon-icons.com/2107/PNG/512/file_type_css_icon_130661.png'
    },
    {
        'id':3,
        'title':'Java Script',
        'discription':'Java Script is used to provide actions for the webpages',
        'imageurl':'data:image/jpeg;base64,'
    }
]
def home(request):
    return render(request,'posts/home.html',{'posts':posts})

def detail(request,id):
    post_data=None
    for post in posts:
        if post['id']==id:
            post_data=post
    if post_data:
        return render(request,'posts/detail.html',{'post':post_data})
    else:
        raise Http404()


